mapper.select:
select * from ( select rownum as RNUM, A.* from ( select b.*, m.mem_name, f.bfile_filename from fp_board b join fp_mem m on b.mem_no=m.mem_no left join fp_boardatc f on b.board_no= f.board_no where b.btype_no=#{btypeNo}
order by board_regdate desc )A ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update fp_board
set board_readcount=board_readcount+1
where board_no=#{boardNo}

update fp_board
set board_title=#{boardTitle}, board_content=#{boardContent}
where board_no=#{boardNo}

update fp_boardatc
set bfile_filename=#{bfileFilename}, bfile_originname=#{bfileOriginname}
where bfile_no=#{bfileNo}
